mapper.select:
select a.seq ,a.title ,a.content ,a.newspaper ,a.abroadNy ,a.event ,a.league ,a.reporter ,a.aDelNy ,a.createdAt ,a.modifiedAt from article a where 1=1
AND aDelNy = #{shDelNy}
AND reporter LIKE CONCAT('%',#{shValue},'%') AND newspaper LIKE CONCAT('%',#{shValue},'%') AND event LIKE CONCAT('%',#{shValue},'%')
AND a.createdAt BETWEEN #{startDate} AND #{endDate}

mapper.insert:
INSERT INTO article (
	title
	,content
	,newspaper
	,abroadNy
	,event
	,league
	,reporter
	,aDelNy
	,createdAt
	,modifiedAt
) 
VALUES (
	#{title}
	,#{content}
	,#{newspaper}
	,#{abroadNy}
	,#{event}
	,#{league}
	,#{reporter}
	,#{aDelNy}
	,now()
	,now()
)

	SELECT last_insert_id()
