mapper.sql:
FROM article a
	inner join user u on u.seq = a.createdBy
WHERE 1=1
AND aDelNy = #{shDelNy}

	AND seq = #{shValue}
	AND newspaper LIKE CONCAT('%',#{shValue},'%')
	AND reporter LIKE CONCAT('%',#{shValue},'%')
	AND event LIKE CONCAT('%',#{shValue},'%')


	AND a.createdAt BETWEEN #{startDate} AND #{endDate}

mapper.select:
select a.seq ,a.title ,a.content ,a.newspaper ,a.abroadNy ,a.event ,a.league ,a.reporter ,a.aDelNy ,a.createdAt ,a.modifiedAt ,u.email as email
ORDER BY a.seq DESC
)aa

mapper.insert:
INSERT INTO article (
	title
	,content
	,newspaper
	,abroadNy
	,event
	,league
	,reporter
	,aDelNy
	,createdAt
	,modifiedAt
) 
VALUES (
	#{title}
	,#{content}
	,#{newspaper}
	,#{abroadNy}
	,#{event}
	,#{league}
	,#{reporter}
	,#{aDelNy}
	,now()
	,now()
)

	SELECT last_insert_id()

mapper.update:
UPDATE article
SET
	title = #{title}
	,content = #{content}
	,newspaper = #{newspaper}
	,abroadNy = #{abroadNy}
	,event = #{event}
	,league = #{league}
	,reporter = #{reporter}
	,modifiedAt = now()
where 1=1
	and seq = #{seq}
