mapper.sql:
FROM article a
	inner join user u on u.seq = a.createdBy
WHERE 1=1
AND aDelNy = #{shDelNy}

	AND aSeq = #{shValue}
	AND newspaper LIKE CONCAT('%',#{shValue},'%')
	AND reporter LIKE CONCAT('%',#{shValue},'%')
	AND aEvent LIKE CONCAT('%',#{shValue},'%')


	AND a.aCreatedAt BETWEEN #{startDate} AND #{endDate}

mapper.select:
select a.seq ,a.title ,a.content ,a.newspaper ,a.aAbroadNy ,a.aEvent ,a.aLeague ,a.reporter ,a.aDelNy ,a.aCreatedAt ,a.aModifiedAt ,u.email as email
ORDER BY a.seq DESC
)aa

mapper.insert:
INSERT INTO article (
	title
	,content
	,newspaper
	,aAbroadNy
	,aEvent
	,aLeague
	,reporter
	,aDelNy
	,aCreatedAt
	,aModifiedAt
) 
VALUES (
	#{title}
	,#{content}
	,#{newspaper}
	,#{abroadNy}
	,#{event}
	,#{league}
	,#{reporter}
	,0
	,now()
	,now()
)

	SELECT last_insert_id()

mapper.update:
UPDATE article
SET
	title = #{title}
	,content = #{content}
	,newspaper = #{newspaper}
	,aAbroadNy = #{abroadNy}
	,aEvent = #{event}
	,aLeague = #{league}
	,reporter = #{reporter}
	,aModifiedAt = now()
where 1=1
	and aSeq = #{seq}
