mapper.select:
select 
	c.*

from 
	code c

where 1=1
	AND ifccDelNy = #{shDelNy}

		AND ccSeq = #{shValue}
		AND ifccName LIKE CONCAT('%',#{shValue},'%')
		AND ccgSeq = #{shValue}

mapper.insert:
INSERT INTO code (
	ifccName
	,ifccAnother
	,ifccNameEng
	,ifccUseNy
	,ifccOrder
	,ifccExplanation
	,ifccDelNy
	,ifccReferencev1
	,ifccReferencev2
	,ifccReferencev3
	,ifccReferenceI1
	,ifccReferenceI2
	,ifccReferenceI3
	,codeGroup_ccgSeq
)
VALUES (
	#{ifccName}
	,#{ifccAnother}
	,#{ifccNameEng}
	,#{ifccUseNy}
	,#{ifccOrder}
	,#{ifccExplanation}
	,#{ifccDelNy}
	,#{referenceV1}
	,#{referenceV2}
	,#{referenceV3}
	,#{referenceI1}
	,#{referenceI2}
	,#{referenceI3}
	,#{ccgSeq}
)


	SELECT last_insert_id()
