mapper.insert:
INSERT INTO code (
	ifccName
	,ifccAnother
	,ifccNameEng
	,ifccUseNy
	,ifccOrder
	,ifccExplanation
	,ifccDelNy
	,ifccReferencev1
	,ifccReferencev2
	,ifccReferencev3
	,ifccReferenceI1
	,ifccReferenceI2
	,ifccReferenceI3
	,codeGroup_ccgSeq
	,createdAt
)
VALUES (
	#{ifccName}
	,#{ifccAnother}
	,#{ifccNameEng}
	,#{ifccUseNy}
	,#{ifccOrder}
	,#{ifccExplanation}
	,#{ifccDelNy}
	,#{referenceV1}
	,#{referenceV2}
	,#{referenceV3}
	,#{referenceI1}
	,#{referenceI2}
	,#{referenceI3}
	,#{ccgSeq}
	,now()
)


	SELECT last_insert_id()

mapper.update:
UPDATE code
SET
	ifccName = #{ifccName}
	,ifccAnother = #{ifccAnother}
	,ifccNameEng = #{ifccNameEng}
	,ifccUseNy = #{ifccUseNy}
	,ifccOrder = #{ifccOrder}
	,ifccExplanation = #{ifccExplanation}
	,ifccDelNy = #{ifccDelNy}
	,ifccReferencev1 = #{referenceV1}
	,ifccReferencev2 = #{referenceV2}
	,ifccReferencev3 = #{referenceV3}
	,ifccReferenceI1 = #{referenceI1}
	,ifccReferenceI2 = #{referenceI2}
	,ifccReferenceI3 = #{referenceI3}
	,modifiedAt = now()
WHERE 1=1
	AND ccSeq = #{ccSeq}
