mapper.select:
select 
	c.*

from 
	CC c

where 1=1
	AND deleteNy = #{shDelNy}

		AND seq = #{shValue}
		AND CCName LIKE CONCAT('%',#{shValue},'%')
		AND CCG_seq = #{shValue}

mapper.insert:
INSERT INTO CC (
	CCName
	,seqAnother
	,useNY
	,ccOrder
	,ReferenceV1
	,ReferenceV2
	,ReferenceI1
	,ReferenceI2
	,CCG_seq
)
VALUES (
	#{CCName}
	,#{seqAnother}
	,#{useNY}
	,#{ccOrder}
	,#{referenceV1}
	,#{referenceV2}
	,#{referenceI1}
	,#{referenceI2}
	,#{ccgSeq}
)


	SELECT last_insert_id()
