mapper.select:
select cg.ccgSeq ,cg.ifcgAnother ,cg.ifcgName ,cg.ifcgNameEng ,cg.ifcgUseNy ,cg.ifcgOrder ,cg.ifcgExplanation ,cg.ifcgDelNy ,cg.ifcgReferencev1 ,cg.ifcgReferencev2 ,cg.ifcgReferencev3 ,cg.ifcgReferenceI1 ,cg.ifcgReferenceI2 ,cg.ifcgReferenceI3 ,cg.createdAt ,cg.modifiedAt ,(select count(c.codeGroup_ccgSeq) from code c where 1=1 and c.codeGroup_ccgSeq = cg.ccgSeq) as count from codeGroup cg where 1=1
AND ifcgDelNy = #{shDelNy}
AND ccgSeq = #{shValue} AND ifcgName LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO codeGroup (
	ifcgName
	,ifcgAnother
	,ifcgNameEng
	,ifcgUseNy
	,ifcgOrder
	,ifcgExplanation
	,ifcgDelNy
	,ifcgReferencev1
	,ifcgReferencev2
	,ifcgReferencev3
	,ifcgReferenceI1
	,ifcgReferenceI2
	,ifcgReferenceI3

)
VALUES (
	#{ifcgName}
	,#{ifcgAnother}
	,#{ifcgNameEng}
	,#{ifcgUseNy}
	,#{ifcgOrder}
	,#{ifcgExplanation}
	,#{ifcgDelNy}
	,#{referenceV1}
	,#{referenceV2}
	,#{referenceV3}
	,#{referenceI1}
	,#{referenceI2}
	,#{referenceI3}
)


	SELECT last_insert_id()
