mapper.select:
(select ac.seq ,ac.comment ,ac.createdAt ,ac.modifiedAt ,ac.DelNy ,u.name as name ,u.id as id ,u.gender as gender from article_comment ac inner join user u on u.seq = ac.createdBy order by ac.article_seq ,ac.createdAt) union all (select gc.seq ,gc.comment ,gc.createdAt ,gc.modifiedAt ,gc.DelNy ,u.name as name ,u.id as id ,u.gender as gender from game_comment gc inner join user u on u.seq = gc.createdBy order by gc.game_seq ,gc.createdAt) order by createdAt
AND DelNy = #{shDelNy}
AND name LIKE CONCAT('%',#{shValue},'%') AND id LIKE CONCAT('%',#{shValue},'%') AND gender = #{shValue}
AND createdAt BETWEEN #{startDate} AND #{endDate}
