mapper.sql:
from game g inner join team t1 on t1.teamSeq = g.team_home inner join team t2 on t2.teamSeq = g.team_away where 1=1 and g.gcDelNy = 0 AND gcDelNy = #{shDelNy}
AND gameDate LIKE CONCAT('%',#{shValue},'%') AND team_home LIKE CONCAT('%',#{shValue},'%') AND team_away LIKE CONCAT('%',#{shValue},'%')
AND gCreatedAt BETWEEN #{startDate} AND #{endDate}

mapper.select:
select g.gSeq ,g.gAbroadNy ,g.gEvent ,g.gLeague ,g.score_home ,g.score_away ,g.player_home ,g.player_away ,g.stadium ,g.gameDate ,g.gameDuration ,g.gCreatedAt ,g.gModifiedAt ,g.gcDelNy ,t1.teamName as team_home ,t2.teamName as team_away
ORDER BY g.gameDate DESC ,g.gameDuration DESC
) aa

mapper.insert:
INSERT INTO game (
	gAbroadNy
	,gEvent
	,gLeague
	,team_home
	,team_away
	,score_home
	,score_away
	,player_home
	,player_away
	,stadium
	,gameDate
	,gameDuration
	,gcDelNy
	,gCreatedAt
	,gModifiedAt
) 
VALUES (
	#{gAbroadNy}
	,#{gEvent}
	,#{gLeague}
	,#{team_home}
	,#{team_away}
	,#{score_home}
	,#{score_away}
	,#{player_home}
	,#{player_away}
	,#{stadium}
	,#{gameDate}
	,#{gameDuration}
	,0
	,now()
	,now()
)

	SELECT last_insert_id()

mapper.update:
UPDATE game 
SET
	gAbroadNy = #{gAbroadNy}
	,gEvent = #{gEvent}
	,gLeague = #{gLeague}
	,score_home = #{score_home}
	,score_away = #{score_away}
	,player_home = #{player_home}
	,player_away = #{player_away}
	,stadium = #{stadium}
	,gameDate = #{gameDate}
	,gameDuration = #{gameDuration}
	,team_home = #{team_home}
	,team_away = #{team_away}
	,gModifiedAt = now()
WHERE 1=1
	and gSeq = #{gSeq}
