mapper.select:
select g.seq ,g.abroadNy ,g.event ,g.league ,g.score_home ,g.score_away ,g.player_home ,g.player_away ,g.stadium ,g.gameDate ,g.gameDuration ,g.createdAt ,g.modifiedAt ,g.gcDelNy ,t1.teamName as team_home ,t2.teamName as team_away from game g inner join team t1 on t1.teamSeq = g.team_home inner join team t2 on t2.teamSeq = g.team_away where 1=1 order by g.gameDate ,g.gameDuration
AND gcDelNy = #{shDelNy}
AND event LIKE CONCAT('%',#{shValue},'%') AND gameDate LIKE CONCAT('%',#{shValue},'%') AND team_home LIKE CONCAT('%',#{shValue},'%') AND team_away LIKE CONCAT('%',#{shValue},'%')
AND g.createdAt BETWEEN #{startDate} AND #{endDate}

mapper.insert:
INSERT INTO game (
	abroadNy
	,event
	,league
	,team_home
	,team_away
	,score_home
	,score_away
	,player_home
	,player_away
	,stadium
	,gameDate
	,gameDuration
	,createdAt
	,modifiedAt
) 
VALUES (
	#{abroadNy}
	,#{event}
	,#{league}
	,#{team_home}
	,#{team_away}
	,#{score_home}
	,#{score_away}
	,#{player_home}
	,#{player_away}
	,#{stadium}
	,#{gameDate}
	,#{gameDuration}
	,now()
	,now()
)

	SELECT last_insert_id()
