mapper.sql:
FROM user u
	inner join teamUser tu on tu.user_seq = u.seq
	inner join team t on t.teamSeq = tu.team_seq
WHERE 1=1
	and tu.defaultNY = 0
AND uDelNy = #{shDelNy}

		AND createdAt BETWEEN #{startDate} AND #{endDate}



	AND seq = #{shValue}
	AND name LIKE CONCAT('%',#{shValue},'%')
	AND id LIKE CONCAT('%',#{shValue},'%')

mapper.select:
select u.seq ,u.name ,u.id ,u.email ,u.gender ,u.dob ,u.radio_operator ,u.phone ,u.zip ,u.address ,u.address_detail ,u.job ,date_format(u.createdAt, '%Y-%m-%d %H:%i:%s') as createdAt ,date_format(u.modifiedAt, '%Y-%m-%d %H:%i:%s') as modifiedAt ,u.user_div ,u.uDelNy ,t.teamName as team
ORDER BY u.seq DESC
) aa

mapper.insert:
INSERT INTO user (
	name
	,id
	,email
	,pwd
	,gender
	,dob
	,radio_operator
	,phone
	,zip
	,address
	,address_detail
	,job
	,createdAt
	,modifiedAt
	,user_div
	,uDelNy
)
VALUES (
	#{name}
	,#{id}
	,#{email}
	,#{pwd}
	,#{gender}
	,#{dob}
	,#{radio_operator}
	,#{phone}
	,#{zip}
	,#{address}
	,#{address_detail}
	,#{job}
	,now()
	,now()
	,25
	,0
)

	SELECT last_insert_id()

INSERT INTO teamUser (
	team_seq
	,user_seq
	,deleteNY
	,defaultNY
)
VALUES (
	#{team}
	,#{seq}
	,0
	,0
)

	SELECT last_insert_id()

mapper.update:
UPDATE user
SET
	name = #{name}
	,id = #{id}
	,dob = #{dob}
	,email = #{email}
	,phone = #{phone}
	,gender = #{gender}
	,job = #{job}
	,zip = #{zip}
	,address = #{address}
	,address_detail = #{address_detail}
	,modifiedAt = now()
	,user_div = #{user_div}
where 1=1
	and seq = #{seq}

UPDATE teamUser
SET
	team_seq = #{team}
where 1=1
	and user_seq = #{seq}
    and defaultNY = 0
